import React, { useState } from "react";
import { View, Text, StyleSheet, FlatList, Image } from "react-native";
import Card from "../../UI/Card";
import FormAppButton from "../../UI/FormAppButton";
import PurchaseDetailTable from "./PurchaseDetailTable";
import RatingsForm from "./RatingsForm";
const MyPurchases = () => {
  const [ratingDisplay, setRatingDisplay] = useState(false);

  const productList = [
    {
      orderID: 1,
      name: "Molteni Outline Chair",
      price: 1500,
      date: "21st January 2021",
      image: {
        uri: "https://image.architonic.com/img_pro2-4/117/4367/outline-04-hr-b.jpg",
      },
      orderStatus: true,
      paymentStatus: true,
    },
    {
      orderID: 2,
      name: "Monay NewCastle Chair",
      price: 1500,
      date: "21st January 2021",
      image: {
        uri: "data:image/jpeg;base64,",
      },
      rating: 5,
      orderStatus: true,
      paymentStatus: true,
    },
  ];

  const ratingFormHandler = () => {
    setRatingDisplay((prevState) => !prevState);
  };
  return (
    <View>
      <FlatList
        showsVerticalScrollIndicator={false}
        data={productList}
        keyExtractor={(product) => product.orderID}
        renderItem={({ item }) => (
          <View style={styles.purchases}>
            <Card
              width={415}
              height={ratingDisplay ? 560 : 300}
              ml={20}
              bg="#fff"
            >
              <View style={styles.productContainer}>
                <Text style={styles.purchaseItemName}>{item.name}</Text>
                <Image source={item.image} style={styles.productImage} />
                <PurchaseDetailTable item={item} />
                {!ratingDisplay && (
                  <View style={styles.btnContainer}>
                    <FormAppButton
                      title="Provide Ratings"
                      onPress={ratingFormHandler}
                      width={200}
                    />
                  </View>
                )}
              </View>
              {ratingDisplay && (
                <RatingsForm ratingFormHandler={ratingFormHandler} />
              )}
            </Card>
          </View>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  purchases: {
    marginTop: 2,
  },
  productContainer: {
    flexDirection: "column",
  },
  productImage: {
    width: 60,
    height: 60,
    borderRadius: 40,
    alignSelf: "center",
  },
  purchaseItemDeatils: {
    marginLeft: 30,
    justifyContent: "space-evenly",
  },
  purchaseItemName: {
    fontWeight: "bold",
    fontSize: 20,
    letterSpacing: 2,
    marginLeft: 85,
    marginBottom: 15,
    marginTop: 5,
  },
  purchasePrice: {
    fontSize: 16,
    marginLeft: 8,
  },
  btnContainer: {
    flexDirection: "row",
    marginTop: 0,
    justifyContent: "space-evenly",
    alignSelf: "flex-end",
    marginRight: 15,
  },
});
export default MyPurchases;
